from django.shortcuts import render, get_object_or_404, render_to_responsefrom django.http import HttpResponsefrom .models import Post, PostDetailfrom django.views import genericfrom django.views.generic import ListView# Create your views here.def post_list(request):    queryset = PostDetail.objects.all()    context = {        'object_list': queryset,    }    return render(request, "blog/index.html", context)# def post_detail(request,slug):##     category = get_object_or_404(PostDetail, slug=slug)##     queryset = PostDetail.objects.all()#     context = {##     'object_list':queryset,#     }#     return render(request, 'blog/view_posts.html', context)#class post_detail(ListView):    template_name = 'blog/view_posts.html'    def get_queryset(self):        self.postdetail = get_object_or_404(PostDetail,  slug=self.kwargs['slug'])        return Post.objects.filter(postDetail=self.postdetail)## def post_list(request):#     return  render_to_response("blog/index.html",{##         'categories':  PostDetail.objects.all(),#         'posts': Post.objects.all(),#     })## def view_category(request, slug):#     category = get_object_or_404(PostDetail, slug=slug)#     return render_to_response('blog/', {#         'category': category,#         'posts': Post.objects.filter(category=category)#     })## def view_post(request, slug):#     posts = get_object_or_404(Post, slug=slug)#     return render_to_response('blog/')